(elem) => {
	$(elem).find('.video-preview').addBack('.video-preview').on('click', function() {
		modal.renderContent(`
			<iframe width="750" height="400"
			src="https://www.youtube.com/embed/IG1srhdIZHk?autoplay=1"
			style="
				position: absolute;
				top: calc(50% - 200px); left: 0;
				border: none;
			">
			</iframe>
		`, show=true);
	});
}

